function addToExtraTbody() {
  const allFiles = [];

  fileNames.forEach(file => {
    const { category, adet, value } = parseFilename(file);
    if (category === "KATALOG" || category === "OZEL") {
      const match = file.match(/ANKARA_(\d+)/); // ANKARA_01, ANKARA_02, vb.
      const sortIndex = match ? parseInt(match[1], 10) : 9999;
      allFiles.push({ category, adet, value, sortIndex });
    }
  });

  const grouped = {
    KATALOG: [],
    OZEL: []
  };

  allFiles.forEach(file => {
    grouped[file.category].push(file);
  });

  Object.keys(grouped).forEach(category => {
    grouped[category].sort((a, b) => a.sortIndex - b.sortIndex);

    grouped[category].forEach((file, i) => {
      const name = `${category}${i + 1}`; // Örn: OZEL1, OZEL2
