document.getElementById("magnettablo").addEventListener("wheel", function(event) {
    const focusedCell = document.activeElement;

    if (focusedCell.matches("td:nth-child(4)[contenteditable='true']")) {
        event.preventDefault();

        const baseText = getBaseText(focusedCell.innerText);
        const currentLabel = getCurrentLabel(focusedCell.innerText);
        const options = ["", "GRAFIK", "PERAKENDE"];
        let currentIndex = options.indexOf(currentLabel);

        if (event.deltaY > 0) {
            currentIndex = (currentIndex + 1) % options.length;
        } else {
            currentIndex = (currentIndex - 1 + options.length) % options.length;
        }

        const newLabel = options[currentIndex];
        focusedCell.innerText = baseText + (newLabel ? " " + newLabel : "");
    }
}, { passive: false });

// Yardımcı fonksiyonlar
function getBaseText(text) {
    return text.replace(/\s*(GRAFIK|PERAKENDE)\s*$/, "").trim();
}

function getCurrentLabel(text) {
    const match = text.trim().match(/(GRAFIK|PERAKENDE)$/);
    return match ? match[1] : "";
}
