// .sagpanelcontainer içindeki inputları oku ve JSON olarak döndür
function getInputsAsJson() {
    const container = document.querySelector('.sagpanelcontainer');
    if (!container) return {};
    const inputs = container.querySelectorAll('input[id]');
    const data = {};
    inputs.forEach(input => {
        const id = input.id;
        data[id] = {
            value: input.value,
            kodlar: kodListeleri[id] || []
        };
    });
    return data;
}




// JSON'u localStorage'a kaydet
function saveInputsToLocalStorage() {
    const data = getInputsAsJson();
    localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
}

// localStorage'dan verileri alıp inputlara uygula
function loadInputsFromLocalStorage() {
    const dataStr = localStorage.getItem(STORAGE_KEY);
    if (!dataStr) return;
    let data;
    try {
        data = JSON.parse(dataStr);
    } catch {
        return;
    }
    const container = document.querySelector('.sagpanelcontainer');
    if (!container) return;
    Object.entries(data).forEach(([id, obj]) => {
    const input = container.querySelector(`input[id="${id}"]`);
    if (input && obj && typeof obj === 'object') {
        input.value = obj.value || '';
    }
});

}

